package org.course.management.controller;import org.course.management.entity.User;import org.course.management.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import java.util.List;@Controller@RequestMapping("/admin")public class AdminController {    @Autowired    private UserService userService;    @GetMapping("/trang-chu")    public String ViewTrangChu(Model model) {        model.addAttribute("currentPage", "trang-chu");        return "admin/home";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        model.addAttribute("currentPage", "khoa-hoc");        return "admin/course";    }    @GetMapping("/chuyen-de")    public String ViewChuyenDe(Model model) {        model.addAttribute("currentPage", "chuyen-de");        return "admin/topic";    }    @GetMapping("/nguoi-dung")    public String ViewNguoiDung(Model model) {        List<User> list = userService.findAllUser();        model.addAttribute("users", list);        model.addAttribute("currentPage", "nguoi-dung");        return "admin/user";    }}