package org.course.management.controller;import org.course.management.entity.Course;import org.course.management.entity.Topic;import org.course.management.entity.User;import org.course.management.service.TopicService;import org.course.management.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import java.util.List;@Controller@RequestMapping("/admin")public class AdminController {    @Autowired    private UserService userService;    @Autowired    private TopicService topicService;    @GetMapping({"", "/trang-chu"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentPage", "trang-chu");        return "admin/home";    }    @GetMapping("/khoa-hoc")    public String listAll(Model model) {        model.addAttribute("currentPage", "khoa-hoc");        return "/admin/course";    }    @GetMapping("/deleteKhoaHoc")    public String deleteKhoaHoc(@RequestParam("id") Integer id, Model model) {        return "redirect:/admin/khoa-hoc";    }    @GetMapping("/chuyen-de")    public String listTopic(Model model) {        List<Topic> list = topicService.getAllTopic();        model.addAttribute("topics", list);        model.addAttribute("currentPage", "chuyen-de");        return "/admin/topic";    }    @GetMapping("/nguoi-dung")    public String listUser(Model model) {        List<User> list = userService.findAllUser();        model.addAttribute("users", list);        model.addAttribute("currentPage", "nguoi-dung");        return "admin/user";    }}