package org.course.management.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;@Controllerpublic class UserController {    @GetMapping({"/", "/trang-chinh"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentNav", "/");        return "user/home";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        model.addAttribute("currentNav", "khoa-hoc");        return "user/course";    }    @GetMapping("/thong-tin-ca-nhan")    public String ViewThongTin() {        return "user/profile";    }    @GetMapping("/video")    public String ViewVideo() {        return "user/video-course";    }    @GetMapping("/lien-he")    public String ViewContact(Model model) {        model.addAttribute("currentNav", "lien-he");        return "user/contact";    }    @GetMapping("/faq")    public String ViewFAQ(Model model) {        model.addAttribute("currentNav", "faq");        return "user/faq";    }    @GetMapping({"/404"})    public String View404() {        return "user/404";    }}