package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Time;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_course_detail")public class CourseDetail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "course_dtl_id")    private int courseDetailId;    @Column(name = "viewed_time")    private Time viewedTime;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "course_id")    @JsonBackReference    private Course course;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "user_id")    @JsonBackReference    private User user;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "enroll_id")    @JsonBackReference    private Enroll enroll;}