package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.fasterxml.jackson.annotation.JsonManagedReference;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_enroll")public class Enroll {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "enroll_id")    public int enrollId;    @Column(name = "is_access")    public boolean isAccess;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "course_id")    @JsonBackReference    private Course course;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "user_id")    @JsonBackReference    private User user;    @OneToMany(mappedBy = "enroll")    @JsonManagedReference    private List<Rate> rates;    @OneToMany(mappedBy = "enroll")    @JsonManagedReference    private List<LessonDetail> lessonDetails;}