package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Time;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_lesson")public class Lesson {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "lesson_id")    public int lessonId;    @Column(name = "title")    public String title;    @Column(name = "video")    public String video;    @Column(name = "about", columnDefinition = "nvarchar(512)")    public String about;    @Column(name = "video_duration")    public String videoDuration;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "course_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Course course;    @OneToMany(mappedBy = "lesson")    private List<LessonDetail> lessonDetails;}