package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Time;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_lesson_detail")public class LessonDetail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "lesson_dtl_id")    public int lessonDetailId;    @Column(name = "viewed_time")    public Time viewedTime;    @Column(name = "note", columnDefinition = "nvarchar(512)")    public String note;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "lesson_id")    @JsonBackReference    private Lesson lesson;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "user_id")    @JsonBackReference    private User user;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "enroll_id")    @JsonBackReference    private Enroll enroll;}