package org.course.management.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_lesson_detail")public class LessonDetail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "lesson_dtl_id")    public int lessonDetailId;    @Column(name = "viewed_time")    public String viewedTime;}