package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Date;import java.time.LocalDateTime;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_rate")public class Rate {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "rate_id")    private int rateId;    @Column(name = "number_star")    private Double numberStar;    @Column(name = "comment", columnDefinition = "nvarchar(255)")    private String comment;    @Column(name = "date_comment")    private LocalDateTime dateComment;    @Column(name = "is_comment")    private boolean isComment;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "user_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private User user;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "course_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Course course;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "enroll_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Enroll enroll;}