package org.course.management.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_topic")public class Topic {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "topic_id")    private int id;    @Column(name = "topic_name", columnDefinition = "nvarchar(512)")    private String name;    @Column(name = "image")    private String image;    @Column(name = "description", columnDefinition = "nvarchar(512)")    private String description;}